package com.mobileapp.recipepal;

import android.os.Bundle;
import android.view.MenuItem;
import android.view.View;

import androidx.activity.EdgeToEdge;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.graphics.Insets;
import androidx.core.view.ViewCompat;
import androidx.core.view.WindowInsetsCompat;
import androidx.navigation.NavController;
import androidx.navigation.fragment.NavHostFragment;
import androidx.navigation.ui.NavigationUI;
import androidx.navigation.Navigation;

import com.google.android.material.bottomnavigation.BottomNavigationView;
import com.mobileapp.recipepal.databinding.ActivityMainBinding;

import java.util.List;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

public class MainActivity extends AppCompatActivity {

    private ActivityMainBinding binding;
    private RecipeDao recipeDao;
    private AppDatabase appDatabase;
    // Need to run database queries on another thread
    private final ExecutorService executorService = Executors.newSingleThreadExecutor();

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        EdgeToEdge.enable(this);

        binding = ActivityMainBinding.inflate(getLayoutInflater());
        View view = binding.getRoot();
        setContentView(view);

        appDatabase = AppDatabase.getDatabase(this);

        recipeDao = appDatabase.recipeDao();

        InsertExampleRecipes();

        NavHostFragment navHostFragment = (NavHostFragment) getSupportFragmentManager().findFragmentById(R.id.nav_host_fragment);
        NavController navController = navHostFragment.getNavController();

        BottomNavigationView bottomNavigationView = findViewById(R.id.bottom_nav);
        NavigationUI.setupWithNavController(bottomNavigationView, navController);

        ViewCompat.setOnApplyWindowInsetsListener(binding.navHostFragment, (v, insets) -> {
            Insets systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars());
            v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom);
            return insets;
        });
    }

    @Override
    public boolean onOptionsItemSelected(MenuItem item) {
        // Get NavController instance
        NavController navController = Navigation.findNavController(this, R.id.nav_host_fragment);

        // Handle navigation item selection
        return NavigationUI.onNavDestinationSelected(item, navController) || super.onOptionsItemSelected(item);
    }

    private void passInsertedIdToNextFragment(long insertedId) {
        NavController navController = Navigation.findNavController(this, R.id.nav_host_fragment);

        // Create a Bundle to pass the insertedId
        Bundle bundle = new Bundle();
        bundle.putInt("recipeId", (int) insertedId);  // Cast insertedId to int as the navigation argument expects it

        // Navigate using the NavController and pass the bundle
        navController.navigate(R.id.recipeFragment, bundle);
    }

    private void InsertExampleRecipes() {

        executorService.execute(new Runnable() {
            @Override
            public void run() {

                // Don't add examples if we already have data in the database
                List<Recipe> recipes = recipeDao.getAllSync();

                if (!recipes.isEmpty()) {
                    return;
                }

                Recipe pancakeRecipe = new Recipe();
                pancakeRecipe.recipeName = "Pancakes";
                pancakeRecipe.Ingredients = "Flour, Eggs, Milk";
                pancakeRecipe.Instructions = "1. Add all ingredients to a bowl, 2. Mix ingredients, 3. Add one pancake worth to a hot pan, 4. Cook for 1 minute on each side, 5. Remove from pan and serve";
                pancakeRecipe.ImageUrl = "data:image/jpeg;base64,";
                recipeDao.insertSingle(pancakeRecipe);

                Recipe LasagnaRecipe = new Recipe();
                LasagnaRecipe.recipeName = "Lasagna";
                LasagnaRecipe.Ingredients = "9 lasagna noodles, 1 tablespoon olive oil, 1 pound ground beef, 1 pound bulk Italian sausage, 1 (16 ounce) can sliced mushrooms, drained, 1 teaspoon garlic salt, 1 teaspoon dried oregano, ½ teaspoon dried thyme, ¼ teaspoon dried basil, 4 (15 ounce) cans tomato sauce, salt and pepper to taste, 1 (15 ounce) container ricotta cheese, 3 large eggs, beaten, ⅓ cup grated Parmesan cheese, 1 pound shredded mozzarella cheese";
                LasagnaRecipe.Instructions = "1. Preheat the oven to 350 degrees F (175 degrees C), 2. Bring a large pot of lightly salted water to a boil. Add lasagna noodles and olive oil. Cook until al dente, 8 to 10 minutes; drain, 3. Cook ground beef and sausage in a large pot over medium heat; drain. Stir in mushrooms, garlic salt, oregano, thyme, basil, and tomato sauce. Season with salt and pepper. Simmer 30 minutes, 4. Meanwhile, mix ricotta cheese, eggs, and Parmesan cheese together in a bowl, 5. Cover the bottom of a 9x13-inch baking dish with a thin layer of sauce. Layer 3 lasagna noodles over sauce. Spread about 1/3 ricotta cheese mixture over noodles. Sprinkle about 1/4 mozzarella cheese over ricotta cheese mixture. Ladle about 1/3 meat sauce over mozzarella cheese. Repeat layering twice more, finishing with remaining mozzarella cheese, 6. Bake in preheated oven until cheese melts and sauce bubbles, about 90 minutes. Cover lasagna with foil if cheese begins to get too dark, 7. Let sit 10 to 15 minutes before serving";
                LasagnaRecipe.ImageUrl = "data:image/jpeg;base64,";
                recipeDao.insertSingle(LasagnaRecipe);
            }
        });
    }
}
